function wwm(s, f) {
	return new Promise(resolve => {
		if (document.querySelector(s)) return resolve(f);
		const observer = new MutationObserver(m => {
			if (document.querySelector(s)) {
				resolve(f);
				observer.disconnect();
			}
		});
		observer.observe(document, {
			childList: true,
			subtree: true
		});
	});
}

function AnimarHeader() {
	if (!document.querySelector(".carrinhoconteudo.aberto") && !document.querySelector(".categorias.aberto")) {
		let currentPostionY = scrollY;
		document.onscroll = function() {
			if (scrollY < currentPostionY) {
				currentPostionY = scrollY;
				document.querySelector(".header").style.removeProperty("transform");
			} else if (scrollY > currentPostionY) {
				currentPostionY = scrollY;
				document.querySelector(".header").style.transform = "translate(0, -40px)";
			}
		};
	}
}

function abrirFull(foto, multiplos) {
	multiplos = multiplos || false;
	document.onscroll = null;
	document.body.style.overflow = "hidden";
	document.querySelector(".verfotofull").classList.add("carregando");
	history.state ?
		(
			history.back(),
			setTimeout(function() {
				wwm(".verfotofull.carregando").then(function() {
					window.history.pushState('forward', 'foto', '#');
					DoIt();
					document.querySelector(".fotofullviewing").classList.add("aberto");
				})
			}, 100)
		) :
		(
			wwm(".verfotofull.carregando").then(function() {
				window.history.pushState('forward', 'foto', '#');
				DoIt();
				document.querySelector(".fotofullviewing").classList.add("aberto");
			})
		);

	function DoIt() {
		if (multiplos) {
			let imgstoload = foto.length;
			document.querySelector(".fotofullviewing").classList.remove("single");
			foto.forEach(function(e, i) {
				if (i == 0) {
					e.startsWith(" ") ? e = e.replace(" ", "") : null;
					document.querySelector(".fotofullviewing img").src = e;
					document.querySelector(".fotofullviewing img").onload = function() {
						if (imgstoload <= 1) {
							document.querySelector(".verfotofull").classList.remove("carregando");
						} else {
							imgstoload = Number(imgstoload - 1);
						}
					};
				} else {
					e.startsWith(" ") ? e = e.replace(" ", "") : null;
					let novaimg = document.createElement("img");
					novaimg.src = e;
					novaimg.draggable = false;
					document.querySelector(".fotofullviewing .asimagensfull").append(novaimg);
					novaimg.onload = function() {
						if (imgstoload <= 1) {
							document.querySelector(".verfotofull").classList.remove("carregando");
						} else {
							imgstoload = Number(imgstoload - 1);
						}
					};
				}
			})
		} else {
			document.querySelector(".fotofullviewing").classList.add("single");
			document.querySelector(".fotofullviewing img").src = foto;
			document.querySelector(".fotofullviewing").querySelector("img").onload = function() {
				document.querySelector(".verfotofull").classList.remove("carregando");
			};
		}
	}
}

