	function arrayRemove(arr, value) {
		return arr.filter(function(ele) {
			return ele != value;
		});
	}

	function criandoslides(path,mutiplos){
		let requests=new Array(),urls=new Array(),aw,op=new XMLHttpRequest(),imagestoload=[];mutiplos=mutiplos||false;
		function addClicks() {
			document.querySelectorAll(".carousel-item").forEach(function(e) {
				e.addEventListener('click', function() {
					let oBsmouseDown = function(e) {
						if (e.target.nodeName == "FIGURE") {
							document.querySelector(".img-fluid.figure-img").classList.remove("aos-animate");
							document.removeEventListener("mousedown", oBsmouseDown, true);
							setTimeout((function() {document.querySelector(".figure").classList.remove("aberto");window.location.href.endsWith("#")?history.back():null;}),180);
						}
					};
					document.addEventListener("mousedown", oBsmouseDown, true);
					window.location.assign("#");
					window.onhashchange = function() {
						if (!window.location.href.endsWith("#")) {
							document.querySelector(".img-fluid.figure-img").classList.remove("aos-animate");
							setTimeout((function() {document.querySelector(".figure").classList.remove("aberto");}),180);
						}
					}
					let e = this.querySelector("img").src;
					document.querySelector(".figure.d-flex").querySelector("img").src = e;
					document.querySelector(".figure.d-flex").classList.add("aberto");
					document.querySelector(".img-fluid.figure-img").classList.add("aos-animate");
				});
			});
		}
		function criarImages() {
			op.open("GET", "https://missstore.github.io/assets/img" + path + "info.txt", true);
			op.onload = function() {
				aw = JSON.parse(op.responseText);
				aw.info.forEach(function(e, i) {
					let ddv = document.createElement("div");
					ddv.setAttribute("class", document.querySelectorAll(".carousel-item").length < 1 ? "carousel-item active" : "carousel-item");
					let novaImg = document.createElement("img");
					novaImg.classList.add("d-block", "w-100");
					novaImg.draggable = false;
					novaImg.src = '/assets/img' + path + e.src;
					let cptxtdesc = document.createElement("div");
					cptxtdesc.classList.add("carousel-caption", "d-none", "d-md-block");
					let novoText = document.createElement("h5");
					novoText.textContent = e.nome;
					let novoDesc = document.createElement("p");
					novoDesc.textContent = "R$" + e.valor;
					cptxtdesc.append(novoText, novoDesc);
					ddv.append(novaImg, cptxtdesc);
					ddv.setAttribute("tags", e.tags);
					ddv.setAttribute("texto", e.nome);
					ddv.setAttribute("preco", e.valor);
					ddv.setAttribute("cores", e.cores);
					ddv.setAttribute("tamanhos", e.tamanhos);
					e.oferta == "sim" ? (ddv.setAttribute("oferta", "sim"), ddv.setAttribute("valorantigo", e.valor_antigo)) : null;
					document.querySelector(".carousel-inner").append(ddv);
					let novoindicator = document.createElement("button");
					novoindicator.setAttribute("data-bs-target", "#MyCarousel");
					let novoNum = 0;
					document.querySelectorAll("[data-bs-slide-to]").forEach(function(e,i){document.querySelector("[data-bs-slide-to='" + i + "']")?novoNum++:0;});
					novoindicator.setAttribute("data-bs-slide-to", novoNum);
					novoindicator.setAttribute("aria-label", "Slide " + novoNum);
					document.querySelectorAll(".carousel-item").length <= 1 ? (novoindicator.classList.add("active"), novoindicator.setAttribute("aria-current", "true")) : null;
					document.querySelector(".carousel-indicators").append(novoindicator);
					let url=novaImg.src,img=new Image();img.src=url;imagestoload.push(img.src);
					img.onload=function(){
						if(imagestoload.length<=1) obterdadosdostorage();
						else imagestoload = arrayRemove(imagestoload, img.src);
					};
					addClicks();
				})
			};
			op.send(null);
		}
		function ProcessUrls(){requests=new Array();for(i=0;i<urls.length;i++){requests.push(new ProcessUrl(urls[i]));}}
		function ProcessUrl(url){
			let http = new XMLHttpRequest();
			http.open("GET", url, true);
			http.onreadystatechange=function(){
				if(http.readyState==4&&http.status==200){
					aw = JSON.parse(http.responseText);paraSub = url;
					aw.info.forEach(function(e,i){
						let ddv = document.createElement("div");
						ddv.setAttribute("class", document.querySelectorAll(".carousel-item").length < 1 ? "carousel-item active" : "carousel-item");
						let novaImg = document.createElement("img");
						novaImg.classList.add("d-block", "w-100");
						novaImg.draggable = false;
						novaImg.src = '/assets/img' + paraSub.substring(paraSub.indexOf("img/") + 3, paraSub.length - 8) + e.src;
						let cptxtdesc = document.createElement("div");
						cptxtdesc.classList.add("carousel-caption", "d-none", "d-md-block");
						let novoText = document.createElement("h5");
						novoText.textContent = e.nome;
						let novoDesc = document.createElement("p");
						novoDesc.textContent = "R$" + e.valor;
						cptxtdesc.append(novoText, novoDesc);
						ddv.append(novaImg, cptxtdesc);
						ddv.setAttribute("tags", e.tags);
						ddv.setAttribute("texto", e.nome);
						ddv.setAttribute("preco", e.valor);
						ddv.setAttribute("cores", e.cores);
						ddv.setAttribute("tamanhos", e.tamanhos);
						e.oferta=="sim"?(ddv.setAttribute("oferta", "sim"),ddv.setAttribute("valorantigo",e.valor_antigo)):null;
						document.querySelector(".carousel-inner").append(ddv);
						let novoindicator = document.createElement("button");
						novoindicator.setAttribute("data-bs-target","#MyCarousel");
						let novoNum = 0;
						document.querySelectorAll("[data-bs-slide-to]").forEach(function(e,i){document.querySelector("[data-bs-slide-to='" + i + "']")?novoNum++:0;});
						novoindicator.setAttribute("data-bs-slide-to", novoNum);
						novoindicator.setAttribute("aria-label", "Slide " + novoNum);
						document.querySelectorAll(".carousel-item").length<=1?(novoindicator.classList.add("active"),novoindicator.setAttribute("aria-current", "true")):null;
						document.querySelector(".carousel-indicators").append(novoindicator);
						let url=novaImg.src,img=new Image();img.src=url;imagestoload.push(img.src);
						img.onload = function() {
							if(imagestoload.length<=1) obterdadosdostorage();
							else imagestoload = arrayRemove(imagestoload, img.src);
						}
					})
					addClicks();
				}
			};
			http.send(null);
		}
		if(mutiplos==true){
			let no=path.split(",");
			for(let i=0;i<no.length;i++){
				urls.push('https://missstore.github.io/assets/img' + no[i] + 'info.txt');
				if (i == no.length - 1) ProcessUrls();
			}
		}else{
			criarImages();
		}
	}
	
