	function wwm(s, f) {
		return new Promise(resolve => {
			if (document.querySelector(s)) return resolve(f);
			const observer = new MutationObserver(m => {
				if (document.querySelector(s)) {
					resolve(f);
					observer.disconnect();
				}
			});
			observer.observe(document, {
				childList: true,
				subtree: true
			});
		});
	}

	function adicionaritem(src, nome, valor, cor, tamanho, quantidade) {
		let divcontentadicionado = document.createElement("div");
		divcontentadicionado.setAttribute("class", "contentadicionado");
		divcontentadicionado.style.opacity = 1;
		let imagedoproduto = document.createElement("img");
		imagedoproduto.setAttribute("class", "rounded img-fluid imagedoproduto");
		imagedoproduto.setAttribute("width", "60px");
		imagedoproduto.setAttribute("height", "60px");
		imagedoproduto.setAttribute("draggable", "false");
		imagedoproduto.src = src;
		imagedoproduto.addEventListener("click", function() {
			let oBsmouseDown = function(e) {
				if (e.target.nodeName == "FIGURE") {
					document.querySelector(".img-fluid.figure-img").classList.remove("aos-animate");
					document.removeEventListener("mousedown", oBsmouseDown, true);
					setTimeout((function() {
						document.querySelector(".figure").classList.remove("aberto");
						window.location.href.endsWith("#") ? history.back() : null;
					}), 180);
				}
			};
			document.addEventListener("mousedown", oBsmouseDown, true);
			window.location.assign("#");
			window.onhashchange = function() {
				if (!window.location.href.endsWith("#")) {
					document.querySelector(".img-fluid.figure-img").classList.remove("aos-animate");
					setTimeout((function() {
						document.querySelector(".figure").classList.remove("aberto");
					}), 180);
				}
			}
			document.querySelector(".figure.d-flex").querySelector("img").src = this.src;
			document.querySelector(".figure.d-flex").classList.add("aberto")
			document.querySelector(".img-fluid.figure-img").classList.add("aos-animate")
		})
		divcontentadicionado.append(imagedoproduto);
		let divinformacaoproduto = document.createElement("div");
		divinformacaoproduto.setAttribute("class", "contains-produtos dados");
		divcontentadicionado.append(divinformacaoproduto);
		let nomeh6 = document.createElement("h6");
		nomeh6.setAttribute("class", "text-truncate nomedoproduto");
		nomeh6.textContent = nome;
		divinformacaoproduto.append(nomeh6);
		let valorh6 = document.createElement("h6");
		valorh6.setAttribute("class", "valordoproduto");
		valorh6.textContent = valor;
		divinformacaoproduto.append(valorh6);
		let coretamanhocarrinho = document.createElement("div");
		coretamanhocarrinho.classList.add("corEtamanhoCarrinho");
		let cordoproduto = document.createElement("div");
		cordoproduto.classList.add("corCarrinho");
		cordoproduto.style.background = cor;
		let tamanhodoproduto = document.createElement("div");
		tamanhodoproduto.classList.add("tamanhoCarrinho");
		tamanhodoproduto.textContent = tamanho;
		coretamanhocarrinho.append(cordoproduto);
		coretamanhocarrinho.append(tamanhodoproduto);
		divinformacaoproduto.append(coretamanhocarrinho);
		let quantidadeinput = document.createElement("input");
		quantidadeinput.type = "number";
		quantidadeinput.setAttribute("class", "quantidadeinput");
		quantidadeinput.required = true;
		quantidadeinput.setAttribute("min", "1");
		quantidadeinput.placeholder = "Quantidade";
		quantidadeinput.value = quantidade;
		quantidadeinput.defaultValue = 1;
		quantidadeinput.addEventListener("change", function() {
			if (isNaN(this.valueAsNumber)) this.value = 1;
			if (document.querySelectorAll(".quantidadeinput").length > 1) {
				let values = [];
				for (let i = 0; i < document.querySelectorAll(".quantidadeinput").length; i++) {
					values.push(document.querySelectorAll(".quantidadeinput")[i].value);
					if (i == document.querySelectorAll(".quantidadeinput").length - 1) {
						document.querySelector(".badge.bg-danger").textContent = eval(values.toString().replaceAll(",", "+"))
						if (document.querySelectorAll(".dropdown-menu .valordoproduto").length > 0) {
							fazersoma();
						}
					}
				}
			} else {
				document.querySelector(".badge.bg-danger").textContent = this.value;
				if (document.querySelectorAll(".dropdown-menu .valordoproduto").length > 0) {
					fazersoma();
				}
			}
			if (this.value <= 0) this.value = 1;
			let srcst = this.parentElement.parentElement.querySelector("img").src;
			let nomest = this.parentElement.parentElement.querySelector(".nomedoproduto").textContent;
			let itemst = this.parentElement.parentElement.querySelector(".nomedoproduto").textContent + "~" +
				this.parentElement.parentElement.querySelector(".corCarrinho").style.background + "~" +
				this.parentElement.parentElement.querySelector(".tamanhoCarrinho").textContent.toUpperCase();
			let valorst = this.parentElement.parentElement.querySelector(".valordoproduto").textContent;
			let corst = this.parentElement.parentElement.querySelector(".corCarrinho").style.background;
			let tamanhost = this.parentElement.parentElement.querySelector(".tamanhoCarrinho").textContent.toUpperCase();
			let novovalorst = this.parentElement.parentElement.querySelector("input").value;
			adicionardadosnostorage(itemst, srcst, nomest, valorst, corst, tamanhost, novovalorst);
		});
		divinformacaoproduto.append(quantidadeinput);
		let divtrash = document.createElement("div");
		divtrash.setAttribute("class", "d-flex trashdiv");
		divcontentadicionado.append(divtrash);
		divtrash.addEventListener("click", function() {
			let elm = this;
			let nome = this.parentElement.querySelector(".nomedoproduto").textContent + "~" +
				this.parentElement.querySelector(".corCarrinho").style.background + "~" +
				this.parentElement.querySelector(".tamanhoCarrinho").textContent.toUpperCase();
			removerdostorage(nome);
			let t = setInterval(function() {
				elm.parentElement.style.opacity -= 0.1;
				if (elm.parentElement.style.opacity == 0) {
					document.querySelector(".badge.bg-danger").classList.remove("invisible");
					document.querySelector(".badge.bg-danger").textContent = Number(document.querySelector(".badge.bg-danger").textContent) - elm.parentElement.children[1].querySelector("input").value;
					elm.parentElement.remove();
					if (document.querySelectorAll(".dropdown-menu .valordoproduto").length > 0) {
						fazersoma();
					}
					let checarsejaexiste = document.querySelector("div.dropdown").querySelectorAll(".nomedoproduto").length;
					if (checarsejaexiste == 0) {
						document.querySelector(".dropdown-menu").classList.add("invisible");
						document.querySelector(".dropdown-menu").classList.remove("rubberBand");
						document.querySelector(".dropdown-menu").classList.remove("animated");
						document.querySelector(".badge.bg-danger").classList.add("invisible");
						document.querySelector(".valortotal").textContent = "Valor total:"
					}
					clearInterval(t);
				}
			}, 50)
		})
		let iconetrash = document.createElement("i");
		iconetrash.setAttribute("class", "far fa-trash-alt");
		divtrash.append(iconetrash);
		divcontentadicionado.append(divtrash);
		document.querySelector(".dropdown-menu").insertBefore(divcontentadicionado,
			document.querySelector(".dropdown-menu").lastElementChild.previousElementSibling.previousElementSibling);
	}

	function adicionardadosnostorage(item, src, nome, valor, cor, tamanho, q) {
		let content = '{"item":[{' +
			'"nome":' + '"' + nome + '",' +
			'"src":' + '"' + src + '",' +
			'"valor":' + '"' + valor + '",' +
			'"cor":' + '"' + cor + '",' +
			'"tamanho":' + '"' + tamanho + '",' +
			'"quantidade":' + '"' + q + '"' + "}]" + "}"
		localStorage.setItem(item, content)
	}

	function removerdostorage(nome) {
		for (let i = 0; i < localStorage.length; i++) {
			if (localStorage.key(i) == nome) {
				localStorage.removeItem(localStorage.key(i))
			}
		}
	}

	function obterdadosdostorage() {
		let checarsejaexiste = document.querySelector("div.dropdown").querySelectorAll(".contentadicionado").length;
		for (let i = 0; i < localStorage.length; i++) {
			try {
				let result = JSON.parse(localStorage.getItem(localStorage.key(i)))
				if (result.item[0]) {
					if (checarsejaexiste > 0) {
						for (let i = 0; i < checarsejaexiste; i++) {
							if (document.querySelectorAll("div.dropdown .contentadicionado .contains-produtos.dados .nomedoproduto")[i].textContent ==
								result.item[0].nome &&
								document.querySelectorAll("div.dropdown .contentadicionado .contains-produtos.dados .corCarrinho")[i].style.background ==
								result.item[0].cor &&
								document.querySelectorAll("div.dropdown .contentadicionado .contains-produtos.dados .tamanhoCarrinho")[i].textContent ==
								result.item[0].tamanho.toUpperCase()) {
								break;
							}
							if (i == checarsejaexiste - 1) {
								document.querySelector(".badge.bg-danger").classList.remove("invisible");
								document.querySelector(".badge.bg-danger").textContent = Number(document.querySelector(".badge.bg-danger").textContent) + 1 * result.item[0].quantidade;
								adicionaritem(result.item[0].src,
									result.item[0].nome,
									result.item[0].valor,
									result.item[0].cor,
									result.item[0].tamanho,
									result.item[0].quantidade);
							}
						}
					} else {
						document.querySelector(".badge.bg-danger").classList.remove("invisible");
						document.querySelector(".badge.bg-danger").textContent = Number(document.querySelector(".badge.bg-danger").textContent) + 1 * result.item[0].quantidade;
						adicionaritem(result.item[0].src,
							result.item[0].nome,
							result.item[0].valor,
							result.item[0].cor,
							result.item[0].tamanho,
							result.item[0].quantidade);
					}
				}
			} catch (e) {
				//console.log(e);
			}
			if (i == localStorage.length - 1) {
				fazersoma();
			}
		}
	}

